  // Handle category selection
  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  // Open the modal
  const handleOpenModal = () => {
    setModalOpen(true);
  };

  // Close the modal
  const handleCloseModal = () => {
    setModalOpen(false);
  };

  // Add new job to listings
  const handlePostJob = (newJob) => {
    setJobListings((prevListings) => [...prevListings, newJob]); // Append new job
    handleCloseModal(); // Close modal after posting
  };

  return (
    <div className="app">
      <Header />
      <button 
        className="new_job_btn-pro" 
        style={{ width: "15%", margin: "20px" }} 
        onClick={handleOpenModal} // Open modal on button click
      >
        + <span>New Job</span>
      </button>

      {isModalOpen && <JobModal onClose={handleCloseModal} onPostJob={handlePostJob} />} {/* Pass onPostJob to the modal */}

      <div style={{ display: 'flex', flexDirection: 'row', width: '100%' }}>
        <Sidebar onCategorySelect={handleCategorySelect} />
        <JobList selectedCategory={selectedCategory} jobListings={jobListings} />
      </div>
      
      <Jobfooter />
    </div>
  );
